import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";
import Header from "../components/Header";
import Card from "../components/Carte";
import { cleanup } from "@testing-library/react";

const Page2 = () => {
  const [listData, setListData] = useState([]);

  useEffect(() => {
    let cardsId = window.localStorage.cards
      ? window.localStorage.cards.split(",")
      : [];

    for (let i = 0; i < cardsId.length; i++) {
      axios
        .get(
          `"https://api.magicthegathering.io/v1/cards/${cardsId.id[i]}"`
        )
        .then((res) => setListData((listData) => [...listData, res.data]));
    }
  }, []);

  return (
    <div className="user-list-page">
      <Header />
      <h2>
        Deck 
      </h2>
      <div className="result">
        {listData.length > 0 ? (
          listData.map((cards) => <Card cards={cards} key={cards.id} />)
        ) : (
          <h2>Aucune carte dans le deck</h2>
        )}
      </div>
    </div>
  );
};

export default Page2;
