import React from "react";

const Card = ({ cards }) => {
/*
  const addStorage = () => {
    let storedData = window.localStorage.cards
      ? window.localStorage.cards.split(",")
      : [];

    if (!storedData.includes(cards.id.toString())) {
      storedData.push(cards.id);
      window.localStorage.cards = storedData;
    }
  };

  const deleteStorage = () => {
    let storedData = window.localStorage.cards.split(",");
    let newData = storedData.filter((id) => id != cards.id);

    window.localStorage.cards = newData;
  };
*/
  return (
    <div className="card">
      <img
        src={
          cards.imageUrl
        }
        alt={`affiche ${cards.name}`}
      />
      <h2>{cards.name}</h2>
      {cards.rarity ? (
        <h5>Edition : {cards.setName}</h5>
      ) : null}
      <h4>
        {cards.type} <span>{cards.manaCost}</span>
      </h4>
      {cards.originalText ? <h3>Synopsis</h3> : ""}
      <p>{cards.originalText}</p>
      {cards.id ? (
        <div className="btn" onClick={() => addStorage()}>
          Ajouter aux panier
        </div>
      ) : (
        <div
          className="btn"
          onClick={() => {
            deleteStorage();
            window.location.reload();
          }}
        >
          Supprimer de la liste
        </div>
      )}
    </div>
  );
};

export default Card;
